'use strict';

/* eslint-disable no-console */
/* eslint-disable quote-props */

const viziaFormat = require('../src');
const intlNumberFormatUnits = require('../src/lib/intl-number-format-units');

const formatFixtures = [
    'number',
    'percent',
    'currency',
    'ordinal',
    'duration',
    ...intlNumberFormatUnits
];

const generateResults = (value, label, locale) => {
    return formatFixtures
        .map((unit) => ({
            'Format': unit,
            'Locale': locale,
            [`${label} value (default)`]: viziaFormat(value, {format: unit, locale}),
            [`${label} value (no abbr)`]: viziaFormat(value, {format: unit, abbr: false, locale}),
            [`${label} value (short unit)`]: viziaFormat(value, {format: unit, unitDisplay: 'short', locale}),
            [`${label} value (long unit)`]: viziaFormat(value, {format: unit, unitDisplay: 'long', locale})
        }));
};

console.table(generateResults(0.123, 'Tiny', 'en-US'));
console.table(generateResults(0.123, 'Tiny', 'de'));
console.table(generateResults(12.345, 'Small', 'en-US'));
console.table(generateResults(12.345, 'Small', 'de'));
console.table(generateResults(123.456, 'Regular', 'en-US'));
console.table(generateResults(123.456, 'Regular', 'de'));
console.table(generateResults(123456.789, 'Large', 'en-US'));
console.table(generateResults(123456.789, 'Large', 'de'));
