'use strict';

module.exports = {
    de: () => 'te',
    en: (value) => {
        if (Math.floor((value % 100) / 10) === 1) {
            return 'th';
        }
        const plural = Math.abs(value % 10);
        switch (plural) {
            case 1:
                return 'st';
            case 2:
                return 'nd';
            case 3:
                return 'rd';
            default:
                return 'th';
        }
    },
    fr: (value) => (value === 1) ? 'er' : 'e',
    other: () => 'ยบ'
};
