'use strict';

/* eslint-disable no-underscore-dangle */

/**
 * Polyfill the Intl API to include pluralrules spec.
 * At current time, Safari does not have support.
 */
require('@formatjs/intl-pluralrules/polyfill');

/**
 * Include locale data for pluralrules spec.
 * These are the locales currently selectable within Vizia Admin.
 */
require('@formatjs/intl-pluralrules/dist/locale-data/de');
require('@formatjs/intl-pluralrules/dist/locale-data/en');
require('@formatjs/intl-pluralrules/dist/locale-data/es');
require('@formatjs/intl-pluralrules/dist/locale-data/fr');
require('@formatjs/intl-pluralrules/dist/locale-data/it');
require('@formatjs/intl-pluralrules/dist/locale-data/pt');

/**
 * Polyfill the Intl API to include unified-numberformat spec.
 * At current time, Chrome supports this natively but Firefox/Safari do not.
 */
require('@formatjs/intl-unified-numberformat/polyfill');

/**
 * Include locale data for unified-numberformat spec.
 * These are the locales currently selectable within Vizia Admin.
 */
if (typeof Intl.NumberFormat.__addLocaleData === 'function') {
    Intl.NumberFormat.__addLocaleData(
        require('@formatjs/intl-unified-numberformat/dist/locale-data/de.json'),
        require('@formatjs/intl-unified-numberformat/dist/locale-data/en.json'),
        require('@formatjs/intl-unified-numberformat/dist/locale-data/es.json'),
        require('@formatjs/intl-unified-numberformat/dist/locale-data/fr.json'),
        require('@formatjs/intl-unified-numberformat/dist/locale-data/it.json'),
        require('@formatjs/intl-unified-numberformat/dist/locale-data/pt.json')
    );
}
