'use strict';

const test = require('ava');

const viziaFormat = require('../src');

/**
 * Other Intl NumberFormat units
 * More thorough tests needed, but this is just to ensure the supported units are wired up properly
 */

test('Acre', (t) => {
    t.is(
        viziaFormat(100, {format: 'acre'}),
        '100ac'
    );
});


