'use strict';

const test = require('ava');

const viziaFormat = require('../src');

/**
 * Invalid input
 */

test('Invalid values', (t) => {
    t.is(
        viziaFormat(null),
        '∅',
        'null returns invalid'
    );
    t.is(
        viziaFormat(),
        '∅',
        'undefined returns invalid'
    );
    t.is(
        viziaFormat(Infinity),
        '∅',
        'Infinity returns invalid'
    );
    t.is(
        viziaFormat(NaN),
        '∅',
        'returns invalid'
    );
    t.is(
        viziaFormat('one'),
        '∅',
        'string that cannot be parsed returns invalid'
    );
    t.is(
        viziaFormat([1, 2, 3]),
        '∅',
        'array returns invalid'
    );
    t.is(
        viziaFormat({value: 1}),
        '∅',
        'object returns invalid'
    );
});
