'use strict';

const test = require('ava');

const viziaFormat = require('../src');

/**
 * HTML formatting
 */

test('HTML output: Currency symbol', (t) => {
    t.is(
        viziaFormat(500, {format: 'currency', currency: 'GBP', output: 'html'}),
        '<span class="viz-format"><span class="viz-format__currency">£</span>500</span>',
        'wraps currency symbol in a span'
    );
});

test('HTML output: Percent symbol', (t) => {
    t.is(
        viziaFormat(5, {format: 'percent', output: 'html'}),
        '<span class="viz-format">500<span class="viz-format__percent">%</span></span>',
        'wraps percent symbol in a span'
    );
});

test('HTML output: Ordinal', (t) => {
    t.is(
        viziaFormat(500, {format: 'ordinal', output: 'html'}),
        '<span class="viz-format">500<span class="viz-format__ordinal">th</span></span>',
        'wraps ordinal in a span'
    );
});

test('HTML output: 1', (t) => {
    t.is(
        viziaFormat(1, {output: 'html'}),
        '<span class="viz-format"><span class="viz-format__one">1</span></span>',
        'wraps 1 in a span'
    );
});

test('HTML output: Null', (t) => {
    t.is(
        viziaFormat(null, {output: 'html'}),
        '<span class="viz-format"><span class="viz-format__invalid">∅</span></span>',
        'wraps ∅ in a span'
    );
});
